+  transition: opacity 1s;
+}
+.sliderBlock_items__showing {
+  opacity: 1;
+  z-index: 3;
+}
+.sliderBlock_items:after {
+  content: "";
+  position: absolute;
+  width: 100%;
+  height: 100%;
+  top: 0;
+  left: 0;
+  background: url("https://github.com/BlackStar1991/CardProduct/blob/master/app/img/decorElements/DecorRectangle.png") 50% 50% no-repeat;
