@@ -0,0 +1,517 @@
+<!doctype html>
+<html lang="en">
+  <head>
+  	<title>Login 07</title>
+    <meta charset="utf-8">
+    <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">
+
+	<link href="https://fonts.googleapis.com/css?family=Lato:300,400,700,900&display=swap" rel="stylesheet">
+
+	<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css">
+	
+	<link rel="stylesheet" href="{{asset('login/login_v17/css/style.css')}}">
+  <style>
+@import url("https://fonts.googleapis.com/css?family=Montserrat:400,800");
+* {
+  box-sizing: border-box;
+}
+
+.containers h1 {
+  font-weight: bold;
+  margin: 0;
+}
+
+.containers h2 {
+  text-align: center;
+}
+
+.containers p {
+  font-size: 14px;
+  font-weight: 100;
+  line-height: 20px;
+  letter-spacing: 0.5px;
+  margin: 20px 0 30px;
+}
+
+.containers span {
+  font-size: 12px;
+}
+
+.containers a {
+  color: #333 !important;
+  font-size: 14px;
+  text-decoration: none;
+  margin: 15px 0;
+}
+
+.containers button {
+  border-radius: 20px;
+  border: 1px solid #303030;
+  background-color: #303030;
+  color: #ffffff;
+  font-size: 12px;
+  font-weight: bold;
+  padding: 12px 45px;
+  letter-spacing: 1px;
+  text-transform: uppercase;
+  transition: all 200ms ease-in;
+}
+
+.containers button:active {
+  transform: scale(0.95);
+}
+
+.containers button:focus {
+  outline: none;
+}
+
+.containers button.ghost {
+  background-color: transparent;
+  border-color: #ffffff;
+  transition: all 200ms ease-in;
+}
+.containers button.ghost:hover {
+  background-color: #ffffff;
+  border-color: #ffffff;
+  color: #303030;
+}
+.button-sign-in:hover {
+  background-color: #f6b14b;
+  border-color: #f6b14b;
+}
+.containers form {
+  background-color: #ffffff;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  flex-direction: column;
+  padding: 0 50px;
+  height: 100%;
+  text-align: center;
+}
+
+.containers input {
+  background-color: #f6f6f6;
+  border: none;
+  padding: 12px 15px;
+  margin: 8px 0;
+  width: 100%;
+}
+.containers select {
+  background-color: #f6f6f6;
+  border: none;
+  padding: 12px 15px;
+  margin: 8px 0;
+  width: 100%;
+}
+
+.containers {
+  background-color: #fff;
+  border-radius: 10px;
+  box-shadow: 0 14px 28px rgba(0, 0, 0, 0.25),
+    0 10px 10px rgba(0, 0, 0, 0.22);
+  position: relative;
+  overflow: hidden;
+  height: 83vh;
+  width: 75%;
+  max-width: 100%;
+  min-height: 480px;
+}
+
+.containers .form-container {
+  position: absolute;
+  top: 0;
+  height: 100%;
+  transition: all 0.6s ease-in-out;
+}
+
+.containers .sign-in-container {
+  left: 0;
+  width: 50%;
+  z-index: 2;
+}
+
+.containers.right-panel-active
+  .sign-in-container {
+  transform: translateX(100%);
+}
+
+.containers .sign-up-container {
+  left: 0;
+  width: 50%;
+  opacity: 0;
+  z-index: 1;
+}
+
+.containers.right-panel-active
+  .sign-up-container {
+  transform: translateX(100%);
+  opacity: 1;
+  z-index: 5;
+  animation: show 0.6s;
+}
+
+@keyframes show {
+  0%,
+  49.99% {
+    opacity: 0;
+    z-index: 1;
+  }
+  50%,
+  100% {
+    opacity: 1;
+    z-index: 5;
+  }
+}
+
+.containers .overlay-container {
+  position: absolute;
+  top: 0;
+  left: 50%;
+  width: 50%;
+  height: 100%;
+  overflow: hidden;
+  transition: transform 0.6s ease-in-out;
+  z-index: 100;
+}
+
+.containers.right-panel-active
+  .overlay-container {
+  transform: translateX(-100%);
+}
+
+.overlay {
+  background: #f6b14b;
+  background: -webkit-linear-gradient(
+    to right,
+    #f6b14b,
+    #f6b14b
+  );
+  background: linear-gradient(
+    to right,
+    #f6b14b,
+    #f6b14b
+  );
+  background-repeat: no-repeat;
+  background-size: cover;
+  background-position: 0 0;
+  color: #ffffff;
+  position: relative;
+  left: -100%;
+  height: 100%;
+  width: 200%;
+  transform: translateX(0);
+  transition: transform 0.6s ease-in-out;
+}
+
+.containers.right-panel-active .overlay {
+  transform: translateX(50%);
+}
+
+.containers .overlay-panel {
+  position: absolute;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  flex-direction: column;
+  padding: 0 40px;
+  text-align: center;
+  top: 0;
+  height: 100%;
+  width: 50%;
+  transform: translateX(0);
+  transition: transform 0.6s ease-in-out;
+}
+
+.overlay-left {
+  transform: translateX(-20%);
+}
+
+.containers.right-panel-active .overlay-left {
+  transform: translateX(0);
+}
+
+.overlay-right {
+  right: 0;
+  transform: translateX(0);
+}
+
+.containers.right-panel-active .overlay-right {
+  transform: translateX(20%);
+}
+
+.containers .social-container {
+  margin: 20px 0;
+}
+
+.containers .social-container a {
+  border: 1px solid #dddddd;
+  border-radius: 50%;
+  display: inline-flex;
+  justify-content: center;
+  align-items: center;
+  margin: 0 5px;
+  height: 40px;
+  width: 40px;
+}
+
+body {
+  background-image: url("https://griyamu.com/assets/image/properti_page/login_page_bg.png");
+  background-repeat: no-repeat;
+  background-attachment: fixed;
+  background-size: cover;
+}
+
+.or {
+  display: flex;
+  flex-direction: row;
+}
+
+.or:before,
+.or:after {
+  content: "";
+  flex: 1 1;
+  border-bottom: 1px solid;
+  opacity: 50%;
+  margin: auto;
+}
+
+.or:before {
+  margin-right: 10px;
+}
+
+.or:after {
+  margin-left: 10px;
+}
+
+#register_mobile3
+  .btn-check:active
+  + .btn-outline-warning,
+.btn-check:checked + .btn-outline-warning,
+.btn-outline-warning.active,
+.btn-outline-warning.dropdown-toggle.show,
+.btn-outline-warning:active {
+  background-color: #ffc107 !important;
+}
+
+.btn-outline-warning {
+  color: #ffc107;
+  background-color: transparent !important;
+  background-image: none;
+  border: 2px solid #ffc107 !important;
+}
+
+.btn-outline-warning:hover {
+  color: #ffc107;
+  background-color: transparent !important;
+  background-image: none;
+}
+
+  </style>
+
+
+	</head>
+	<body style="background-image:url('{{asset('images/login/background-login.jpg')}}'); background-size:contain;">
+
+		<div class="outer-container mt-5 mb-5 desktop " style="width:100%;padding:10px;">
+			<div class="containers right-panel-active" id="containers" style="margin:auto;margin-top:50px;">
+				<div class="form-container sign-up-container">
+					<form class="form_register" method="POST" enctype="multipart/form-data" style="align-items:baseline;text-align:left;">
+						<h1 class="title-creat-account">Create Account</h1>
+						<span class="title-creat-account2">or use your email for registration</span><br>
+						<!-- register 1 -->
+						<div id="register1" style="display:block;">
+							<span style="float: right !important;">1/3</span>
+							<input style="font-size:12px;" id="nama" type="text" placeholder="Name" name="nama">
+							<input style="font-size:12px;" id="email" type="email" placeholder="Email" name="email">
+							<input style="font-size:12px;" id="password" type="password" placeholder="Password" name="password"><br><br>
+							<button class="button-sign-in" id="next1">Lanjutkan <i class="fas fa-chevron-right"></i></button>
+						</div>
+						<!-- register 2 -->
+						<div id="register2" style="display:none;width:100%;">
+							<span style="float: right !important;">2/3</span>
+							<input style="font-size:12px;" id="notelp" type="text" placeholder="Phone" name="notelp">
+							<select name="provinsi" class="provinsi" style="height:fit-content;">
+								<option value="">Pilih Provinsi</option>
+								<option value="1">BANTEN</option><option value="2">DKI JAKARTA</option><option value="3">JAWA BARAT</option><option value="4">JAWA TENGAH</option><option value="5">DI YOGYAKARTA</option><option value="6">JAWA TIMUR</option><option value="7">BALI</option><option value="8">NANGGROE ACEH DARUSSALAM (NAD)</option><option value="9">SUMATERA UTARA</option><option value="10">SUMATERA BARAT</option><option value="11">RIAU</option><option value="12">KEPULAUAN RIAU</option><option value="13">JAMBI</option><option value="14">BENGKULU</option><option value="15">SUMATERA SELATAN</option><option value="16">BANGKA BELITUNG</option><option value="17">LAMPUNG</option><option value="18">KALIMANTAN BARAT</option><option value="19">KALIMANTAN TENGAH</option><option value="20">KALIMANTAN SELATAN</option><option value="21">KALIMANTAN TIMUR</option><option value="22">KALIMANTAN UTARA</option><option value="23">SULAWESI BARAT</option><option value="24">SULAWESI SELATAN</option><option value="25">SULAWESI TENGGARA</option><option value="26">SULAWESI TENGAH</option><option value="27">GORONTALO</option><option value="28">SULAWESI UTARA</option><option value="29">MALUKU</option><option value="30">MALUKU UTARA</option><option value="31">NUSA TENGGARA BARAT (NTB)</option><option value="32">NUSA TENGGARA TIMUR (NTT)</option><option value="33">PAPUA BARAT</option><option value="34">PAPUA</option>                    </select>
+							<select name="kota" class="kota">
+								<option value="">Pilih Kota</option>
+							</select><br><br>
+							<button class="button-sign-in" id="next2">Lanjutkan <i class="fas fa-chevron-right"></i></button>
+						</div>
+						<!-- register 3 -->
+						<div id="register3" style="display:none;width:100%;">
+							<span style="float: right !important;">3/3</span>
+							<input value="pasang_iklan" type="checkbox" class="btn-check" id="btn-check" autocomplete="off" name="pilih_kebutuhan">
+							<label class="btn btn-outline-warning" for="btn-check" style="color:#303030;">Pasang Iklan</label>
+		
+							<input value="beli_rumah_cash_atau_kpr" type="checkbox" class="btn-check" id="btn-check2" autocomplete="off" name="pilih_kebutuhan">
